mapper.select:
SELECT id, name, isAdmin, contents, createTime, views, categoryId, userId, updateTime
FROM post
WHERE id = #{id}

SELECT id, name, isAdmin, contents, createTime, views, categoryId, userId, updateTime
FROM post
WHERE userId = #{userId}

SELECT id, name, isAdmin, contents, createTime, views, categoryId, userId, updateTime
FROM post
WHERE userId = #{userId}
ORDER BY createTime DESC
    LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM post
WHERE userId = #{userId}
